(744, 'adjetivo', 7, 153, NULL, '2018-04-28 18:28:04', '2018-05-17 14:49:58'),
(745, 'advérbio', 0, 153, NULL, '2018-04-28 18:28:19', '2018-04-28 18:28:19'),
(746, 'conjunção', 0, 153, NULL, '2018-04-28 18:28:34', '2018-04-28 18:28:34'),
